import styled from 'styled-components';

export const Container = styled.div`
    header {
        text-align: center;
        color: #ffffff;
    }

    .search-section {
        margin-top: 50px;
        text-align: center;
    }

    .results-section {
        display: flex;
        justify-content: space-between;
        margin-top: 30px;
    }

    .track-list {
        display: flex;
        flex-direction: column;
    }

    .track-info {
        display: flex;
        align-items: center;
        margin-bottom: 10px;

        img {
            width: 50px;
            height: 50px;
            border-radius: 5px;
            margin-right: 10px;
        }

        .track-details {
            p {
                margin: 0;
            }
        }
    }

    button {
        padding: 10px 20px;
        border: none;
        background-color: #007bff;
        color: white;
        cursor: pointer;
        border-radius: 5px;

        &:hover {
            background-color: #0056b3;
        }
    }

    .tracklist {
        display: flex;
        flex-direction: column;
    }

    .track {
        margin-bottom: 10px;
    }
`;
