import styled from 'styled-components';

export const Container = styled.div`
    font-family: Arial, sans-serif;
    padding: 20px;
    max-width: 1200px;
    margin: 0 auto;
    header {
        text-align: center;
        color: #ffffff;
    }

    .search-section {
        margin-top: 50px;
        text-align: center;
    }

    .tracks {
        display: flex;
        justify-content: space-between;
    }

    .fifty-percent {
        width: 48%;
    }
`;
