import React, { useState } from 'react';

const App = () => {
  const [accessToken, setAccessToken] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [tracks, setTracks] = useState([]);
  const [errorMessage, setErrorMessage] = useState('');

  // Function to get access token from URL (OAuth callback handling)
  const getAccessToken = () => {
    const hash = window.location.hash;
    let token = null;

    if (hash) {
      const params = new URLSearchParams(hash.substring(1)); // Remove the '#' from the hash
      token = params.get('access_token');
      window.history.pushState('Access Token', null, '/'); // Remove the token from the URL for security
    }

    return token;
  };

  // Handle search request
  const searchTracks = (term) => {
    const token = getAccessToken(); // Get the token from URL

    if (token) {
      const url = `https://api.spotify.com/v1/search?q=${term}&type=track&limit=10`;

      fetch(url, {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
        .then((response) => {
          if (!response.ok) {
            throw new Error('API request failed');
          }
          return response.json();
        })
        .then((data) => {
          // Safely handle data response
          if (data.tracks && data.tracks.items) {
            setTracks(data.tracks.items);
            setErrorMessage('');
          } else {
            setTracks([]);
            setErrorMessage('No tracks found.');
          }
        })
        .catch((error) => {
          console.error('Error fetching tracks:', error);
          setErrorMessage('Failed to fetch tracks. Please try again.');
        });
    } else {
      setErrorMessage('Access token not found.');
    }
  };

  // Handle search term input change
  const handleSearchInputChange = (e) => {
    setSearchTerm(e.target.value);
  };

  // Handle search form submission
  const handleSearchSubmit = (e) => {
    e.preventDefault();
    searchTracks(searchTerm);
  };

  // Fetch access token when the page loads
  React.useEffect(() => {
    const token = getAccessToken();
    if (token) {
      setAccessToken(token);
    }
  }, []);

  return (
    <div className="App">
      <h1>Jammming App</h1>
      {!accessToken ? (
        <p>Please log in to Spotify</p>
      ) : (
        <div>
          <form onSubmit={handleSearchSubmit}>
            <input
              type="text"
              placeholder="Search for a song"
              value={searchTerm}
              onChange={handleSearchInputChange}
            />
            <button type="submit">Search</button>
          </form>

          {errorMessage && <p>{errorMessage}</p>}

          <div className="track-list">
            {tracks.length > 0 ? (
              tracks.map((track) => (
                <div key={track.id} className="track-item">
                  <img src={track.album.images[0]?.url} alt={track.name} width="50" />
                  <div>
                    <h3>{track.name}</h3>
                    <p>{track.artists[0]?.name}</p>
                    <p>{track.album.name}</p>
                  </div>
                </div>
              ))
            ) : (
              <p>No tracks to display</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
