import React, { useState } from 'react';
import { Container } from './styles';
import SearchBar from './components/SearchBar';
import SearchBtn from './components/SearchBtn';
import SearchResults from './components/SearchResults';
import Tracklist from './components/Tracklist';
import axios from 'axios';

export default function App() {
    const [results, setResults] = useState([]);

    // Function to handle search
    const handleSearch = async (query) => {
        try {
            const response = await axios.get(`https://freemusicarchive.org/api/get/tracks.json?limit=10&search=${query}`);
            setResults(response.data.data); // Update results with tracks data
        } catch (error) {
            console.error('Error fetching tracks:', error);
        }
    };

    return (
        <Container>
            <header>
                <h1>Ja<span>mmm</span>ing</h1>
            </header>
            <main>
                <section className="search-section">
                    <SearchBar onSearch={handleSearch} />
                    <SearchBtn onClick={() => handleSearch('')} /> {/* Make sure to pass the query */}
                </section>
                <section className="tracks">
                    <div className="fifty-percent">
                        <SearchResults results={results} />
                    </div>
                    <div className="fifty-percent">
                        <Tracklist />
                    </div>
                </section>
            </main>
        </Container>
    );
}
